sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    if(!event.payload.sys_id){
        return;
    }

    var actionLink = event.payload.table + ".do?sys_id=" + event.payload.sys_id;
    console.log("Link:\n" + actionLink);
    helpers.navigate.to(actionLink);
}
